mapper.select:
select users.id,users.open_id, users.campus_id,nickname, headimg_url, language,province,groupid,country, city,sex,remark, total_amount, phone, address as default_address from users left join usersinfo on users.open_id = usersinfo.open_id
users.campus_id=#{campusId,jdbcType=INTEGER}
and (nickname like #{search,jdbcType=VARCHAR} or usersinfo.phone like #{search,jdbcType=VARCHAR} or usersinfo.address like #{search,jdbcType=VARCHAR})
order by ${sort}
${order}
limit #{offset,jdbcType=INTEGER} , #{limit,jdbcType=INTEGER}

select count("*") from users left join usersinfo on users.open_id = usersinfo.open_id
users.campus_id=#{campusId,jdbcType=INTEGER} and users.campus_admin=#{campusAdmin,jdbcType=VARCHAR}
and (nickname like #{search,jdbcType=VARCHAR} or usersinfo.phone like #{search,jdbcType=VARCHAR} or usersinfo.address like #{search,jdbcType=VARCHAR})

select count("*") from users left join usersinfo on users.open_id = usersinfo.open_id
users.campus_id=#{campusId,jdbcType=INTEGER}
and (nickname like #{search,jdbcType=VARCHAR} or usersinfo.phone like #{search,jdbcType=VARCHAR} or usersinfo.address like #{search,jdbcType=VARCHAR})

mapper.update:
update users
nickname=#{nickname,jdbcType=VARCHAR}, headimg_url=#{headImgUrl,jdbcType=VARCHAR}, language=#{language,jdbcType=VARCHAR}, province=#{province,jdbcType=INTEGER}, country=#{country,jdbcType=VARCHAR}, city=#{city,jdbcType=VARCHAR}, sex=#{sex,jdbcType=SMALLINT},
users.campus_id=#{campusId,jdbcType=INTEGER} and users.open_id=#{openId,jdbcType=VARCHAR}
